    def to_ham(qiskit_op):
        coeffs = []
        ops = []
        for op in qiskit_op:

            coeffs.append(op.coeffs)
            ops.append(str(op.paulis[0]) )

        ops = [qml.pauli.string_to_pauli_word(op) for op in ops]
        H = qml.Hamiltonian(coeffs,ops)

        return H
    pools_uni = [to_ham(ops) for ops in pool]

    return Hamiltonian, pools_uni

    import pennylane as qml

    # Read the file
    with open("operatorB/operator_pool.txt", "r") as f:
        lines = f.readlines()

    # Initialize storage
    pools_uni = []
    coeffs, ops = [], []

    # Process lines
    for line in lines:
        line = line.strip()

        if line == "---":
            # End of a block, create Hamiltonian
            if coeffs and ops:
                ops = [qml.pauli.string_to_pauli_word(op) for op in ops]
                H = qml.Hamiltonian(coeffs, ops)
                pools_uni.append(H)

            # Reset for the next block
            coeffs, ops = [], []

        elif line:
            parts = line.split()
            if len(parts) == 3:
                pauli_str, coeff, _ = parts
                coeffs.append(float(coeff))
                ops.append(pauli_str)

    # If there's a last block (without a trailing ---), add it
    if coeffs and ops:
        ops = [qml.pauli.string_to_pauli_word(op) for op in ops]
        H = qml.Hamiltonian(coeffs, ops)
        pools_uni.append(H)

    # Now `hamiltonians` contains all the Hamiltonians from the file


    return Hamiltonian, pools_uni
